pragma solidity ^0.5.0;

import './SinglePoolCollat.sol';

contract CollateralPool {
    uint public poolId;
    uint public startingBalance;
    uint public currentBalance;
    address public poolOwner;
    address public linkedCollat;
    uint public creationTime;
    uint public endDate;
    // pool status {OPEN, CLOSED}
    // details of exposure layer


    constructor(uint _poolId, address _poolOwner, uint _duration) public payable {
        poolId = _poolId;
        poolOwner = _poolOwner;
        startingBalance = 0;
        currentBalance = 0;
        SinglePoolCollat newCollat = new SinglePoolCollat(poolOwner, poolId);
        linkedCollat = address(newCollat);
        creationTime = now;
        endDate = creationTime + _duration;
        
        // TODO
        // push end time/date to Collat
    }

    
    function topUpPool() public payable {
        currentBalance += msg.value;
    }
    
    function() external payable  {
        startingBalance += msg.value;
        currentBalance += msg.value;
    }
    // function withdraw() {
        // Only when status is correct
        // Only after end }
    // function closeCollat() {} - requests closure of collateral
    // modifier status
    // modifier isLinkedCollat() - only linkedCollat can do margin call
    // event checkTime
    // event topUpCall
}
